package com.bridgelabz.dayoneproblems;

public class DefaultValuesOfPrimitive {
	
	byte b;
	short s;
	char c;
	int i;
	float f;
	long l;
	double d;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		DefaultValuesOfPrimitive val = new DefaultValuesOfPrimitive();
		
		
		System.out.println(val.b);
		System.out.println(val.s);
		System.out.println(val.c);
		System.out.println(val.i);
		System.out.println(val.f);
		System.out.println(val.l);
		System.out.println(val.d);
		
	}

}
